section.div:
var

    cash,save,spend,i:integer;

begin

    cash:=0;

    save:=0;

    for i:=1 to 12 do

        begin

            read(spend);

            cash:=cash+300-spend;

            if cash<0 then//Judge whether the money is enough or not

                begin

                    write('-',i);

                    halt;

                end;

            save:=save+cash div 100;//Save the hundreds of money

            cash:=cash mod 100;

        end;

    cash:=cash+save*120;

    write(cash);

end.

#include<cstdio>

int main()

{

    int cash=0,save=0,spend;

    for(int i=0;i<12;i++)

    {

        scanf("%d",&spend);

        cash+=300-spend;

        if(cash<0)//Judge whether the money is enough or not

        {

            printf("-%d",i+1);//The i is from 0 to 11, so don't forget to add 1

            return 0;

        }

        save+=cash/100;//Save the hundreds of money

        cash%=100;

    }

    cash+=save*120;

    printf("%d",cash);

    return 0;

}
