section.div:
var

    a,b,g,k:array[1..10000]of longint;

    n,i:integer;

    x,y:longint;

begin

    read(n);

    for i:=1 to n do

        read(a[i],b[i],g[i],k[i]);

    read(x,y);

    for i:=n downto 1 do//Remember that we need to output the top one

        if (a[i]<=x)and(b[i]<=y)and(a[i]+g[i]>=x)and(b[i]+k[i]>=y) then//If it has covered the coordinate, output it then exit

            begin

                write(i);

                halt;

            end;

    write(-1);//If no carpet has covered the coordinate, output -1

end.

#include<cstdio>

long a[10000],b[10000],g[10000],k[10000];

int main()

{

    int n;

    scanf("%d",&n);

    for(int i=0;i<n;i++)

        scanf("%ld%ld%ld%ld",&a[i],&b[i],&g[i],&k[i]);

    long x,y;

    scanf("%ld%ld",&x,&y);

    for(int i=n-1;i>=0;i--)//Remember that we need to output the top one

        if((a[i]<=x)&&(b[i]<=y)&&(a[i]+g[i]>=x)&&(b[i]+k[i]>=y))//If it has covered the coordinate, output it then exit

        {

            printf("%d",i+1);//In C/C++, the array indexes are from 0 to n-1, so do not forget to add 1 when you output the index

            return 0;

        }

    printf("-1");//If no carpet has covered the coordinate, output -1

    return 0;

}
